class Solution(object):
    def isPalindrome(self,s): 
        return s == s[::-1] 

    def longestPalindrome(self, s):
        """
        :type s: str
        :rtype: str
        """
        max = ""
        length = -1
        i = 0 
        j = 0

        n = len(s)

        for i in range(0,n):
            for j in range(i+1,n+1):
                if j-i < length: # if current max palindrome length is greater than j-i then skip this iteration
                    continue

                substr = s[i:j]
                if self.isPalindrome(substr):
                    if(length<j-i):
                        max = substr
                        length = j-i
                    
        return max

s = Solution()
print(s.longestPalindrome(""))


